<script setup lang="ts">
import { onMounted, reactive, ref } from "vue";
import PageBasic from "/@/components/pageBasic/pageBasic.vue";
import { message } from "ant-design-vue";
import { removeT } from "@blog/utils";
import {Photo, photoApi, PhotoCategory, photoCategoryApi, PhotoTvSeries, photoTvSeriesApi} from "/src/api/photoApis.ts";
import AddEditModal from "/@/views/photoManage/photo/components/addEditModal.vue";

interface FormState {
  photoCategoryId: number | undefined;
  tvSeriesId: number | undefined;
  author: string;
  photoUrl: string;
  previewUrl: string;
}

const formState = reactive<FormState>({
  photoCategoryId: undefined,
  tvSeriesId: undefined,
  author: "",
  photoUrl: "",
  previewUrl: "",
});

const pageLoading = ref(false);

const getList = () => {
  pageLoading.value = true;
  photoApi()
    .list(formState)
    .then((res: RequestResponse<Array<Photo>>) => {
      if (res.status === 200) {
        dataSource.value = removeT<Photo>(res.data);
      }
    })
    .finally(() => {
      pageLoading.value = false;
    });
};

const dataSource = ref<Array<Photo>>([]);

const columns = [
  {
    title: "预览图",
    dataIndex: "previewUrl",
    key: "previewUrl",
    width: '130px'
  },
  {
    title: "分类",
    dataIndex: "photoCategoryName",
    key: "photoCategoryName",
  },
  {
    title: "影集",
    dataIndex: "tvSeriesName",
    key: "tvSeriesName",
  },
  {
    title: "作者",
    dataIndex: "author",
    key: "author",
  },
  {
    title: "喜欢",
    dataIndex: "like",
    key: "like",
  },
  {
    title: "创建时间",
    dataIndex: "createTime",
    key: "createTime",
  },
  {
    title: "修改时间",
    dataIndex: "updateTime",
    key: "updateTime",
  },
  {
    title: "操作",
    key: "action",
    width: "150px",
  },
];
const editHandler = (val: Photo) => {
  addEditModalRef.value.open(val.id);
};
const delRecord = (record: Photo) => {
  photoApi()
    .del(record.id)
    .then((res: RequestResponse<string>) => {
      if (res.status === 200) {
        message.success(res.message);
        getList();
      }
    });
};

const addEditModalRef = ref();
const add = () => {
  addEditModalRef.value.open();
};

const tvSeriesList = ref<PhotoTvSeries[]>()
const categoryList = ref<PhotoCategory[]>()

onMounted(() => {
  photoCategoryApi().list({categoryName:''}).then((res:RequestResponse<PhotoCategory[]>)=>{
    if (res.status === 200){
      categoryList.value = res.data
    }
  })
  photoTvSeriesApi().list({name:''}).then((res:RequestResponse<PhotoTvSeries[]>)=>{
    if (res.status === 200){
      tvSeriesList.value = res.data
    }
  })
  getList();
});
</script>

<template>
  <page-basic
    v-model="formState"
    :loading="pageLoading"
    @reset="getList"
    @search="getList"
  >
    <template #formItem>
      <a-form-item label="分类" name="photoCategoryId">
        <a-select
            placeholder="分类"
            style="width: 183px"
            v-model:value="formState.photoCategoryId"
        >
          <a-select-option v-for="i in categoryList" :key="i.id" :value="i.id">{{i.categoryName}}</a-select-option>
        </a-select>
      </a-form-item>
      <a-form-item label="影集" name="tvSeriesId">
        <a-select
            placeholder="影集"
            style="width: 183px"
            v-model:value="formState.tvSeriesId"
        >
          <a-select-option v-for="i in tvSeriesList" :key="i.id" :value="i.id">{{i.name}}</a-select-option>
        </a-select>
      </a-form-item>
      <a-form-item label="作者" name="author">
        <a-input placeholder="作者" v-model:value="formState.author"></a-input>
      </a-form-item>
    </template>
    <template #btn>
      <a-button @click="add">新增</a-button>
    </template>
    <a-table
      :loading="pageLoading"
      bordered
      sticky
      :dataSource="dataSource"
      :columns="columns"
      :pagination="false"
    >
      <template #bodyCell="{ column, record }">
        <template v-if="column.key === 'previewUrl'">
          <a-image
              :width="100"
              :src="record.photoUrl"
              fallback="data:image/png;base64,"
          >
            <template #placeholder>
              <a-image
                  :src="record.previewUrl"
                  :width="100"
                  :preview="false"
                  fallback="data:image/png;base64,"
              />
            </template>
          </a-image>
        </template>
        <template v-else-if="column.key === 'action'">
          <div class="flex items-center">
            <a-button type="link" @click="editHandler(record)">编辑</a-button>
            <a-popconfirm
              v-if="dataSource.length"
              title="确定删除?"
              @confirm="delRecord(record)"
            >
              <a-button danger type="link">删除</a-button>
            </a-popconfirm>
          </div>
        </template>
      </template>
    </a-table>
  </page-basic>
  <add-edit-modal ref="addEditModalRef" @success="getList" :category-list="categoryList" :tv-series-list="tvSeriesList" />
</template>

<style scoped lang="less"></style>