<script setup lang="ts">
import { nextTick, onMounted, ref } from "vue";
import { photoApis, PhotoTvSeries } from "/@/api/photo.ts";

const photoTvSeriesList = ref<PhotoTvSeries[]>();

const imgWrapRefs = ref<Element[]>([]);

function loadImg(){
  for (const imgWrap of imgWrapRefs.value) {
    const img = document.createElement('img')
    img.style.objectFit = 'cover'
    img.style.width = '100%'
    img.style.height = '100%'
    img.classList.add('target-scale')
    img.src = imgWrap.getAttribute('data-preurl')!
    img.onload = function (){
      imgWrap.style.backgroundColor = 'unset'
    }
    img.onerror = function (){
      img.src = "data:image/png;base64,"
    }

    const highQualityImg = document.createElement('img')
    highQualityImg.style.objectFit = 'cover'
    highQualityImg.style.width = '100%'
    highQualityImg.style.height = '100%'
    highQualityImg.classList.add('target-scale')
    highQualityImg.src = imgWrap.getAttribute('data-url')!

    highQualityImg.onload = function (){
      img.src = highQualityImg.src
    }

    imgWrap.appendChild(img)
  }
}

onMounted(() => {
  photoApis()
    .tvSeries()
    .then((res: RequestResponse<PhotoTvSeries[]>) => {
      if (res.status === 200) {
        photoTvSeriesList.value = res.data;

        nextTick(() => {
          loadImg()
        });
      }
    });
});
</script>

<template>
  <div class="grid grid-cols-1 sm:grid-cols-2 gap-20px md:grid-cols-3">
    <div v-for="(i, idx) in photoTvSeriesList" :key="i.id">
      <div
        class="h-260px rounded-4px img-wrapper"
        :data-url="i.coverImage"
        :data-preurl="i.previewImage"
        :ref="
          (e: any) => {
            if (e) imgWrapRefs[idx] = e;
          }
        "
      ></div>
      <div class="flex justify-between m-t-8px text-14px">
        <span class="color-#6b7280">{{ i.name }}</span>
        <span class="color-#6b7280">{{ i.author }}</span>
      </div>
    </div>
  </div>
</template>

<style scoped lang="less">
.dark .img-wrapper {
  background-color: #f3f3f3;
  border: 1px solid #6b7280;
  cursor: pointer;
  overflow: hidden;
}
.light .img-wrapper {
  background-color: #f3f3f3;
  border: 1px solid #e5e7eb;
  cursor: pointer;
  overflow: hidden;
}
</style>
